import react from "react";
import "./MainPage.css";
import profilePicture from "../images/SD.jpg";
import { useState } from "react";
import { useEffect } from "react";
import WorkDisplayTemplate from "./WorkDisplayTemplate";
import downArrow from "../images/downArrow.png";
import rightArrow from "../images/rightArrow.png";
import leftArrow from "../images/leftArrow.png";
import upArrow from "../images/upArrow.png";
import EducationDisplayTemplate from "./EducationDisplayTemplate";
import ProjectTemplate from "./ProjectTemplate";

function MainPage() {
  const [darkMode, setDarkMode] = useState(false);
  const [hungarian, setHungarian] = useState(false);
  const [edu, setEdu] = useState(false);
  const [slideIndex, setSlideIndex] = useState(1);
  const [time, setTime] = useState([]);
  const [animation, setAnimation] = useState(false);
  const [def, setDef] = useState(true);
  let name;
  let selected;
  let downA;
  let upA;
  // A projekt adatok. -------------------------------------------------------------------------------------------------------------------------------------
  let dataSlider = [
    {
      pName: "projekt",
      pDescription: "a projekt r√∂vid le√≠r√°sa.",
      pTechnologies: ["HTML", "css", "js", "react"],
      pLinks: "github link",
    },
    {
      pName: "projekt2",
      pDescription: "a projekt r√∂vid le√≠r√°sa.2",
      pTechnologies: ["HTML", "css", "js", "react", "2"],
      pLinks: "github",
    },
  ];
  //---------------------------------------------------------------------------------------------------------------------------------------------

  // onclick active dots-----------------------------------------

  const ActiveDot = (event) => {
    setDef(false);
    let firstClear = document.querySelectorAll("#active");
    console.log(firstClear);
    firstClear.forEach((el) => {
      el.id = "inactive";
    });
    AnimatedBox();
    name = event.target.className;
    selected = document.querySelector(`.${name}`);
    selected.id = "active";
    setHaveActive(true);
    setSlideIndex(Number(name.charAt(name.length - 1)));
  };

  // to the arrow clicking functions
  const ActiveDotWithArrows = (number) => {
    setDef(false);
    let firstClear = document.querySelectorAll("#active");
    console.log(firstClear);
    firstClear.forEach((el) => {
      el.id = "inactive";
    });
    name = `dot${number}`;
    selected = document.querySelector(`.${name}`);
    selected.id = "active";
    setHaveActive(true);
  };
  // ---------------------------------------------------------------------
  // slider arrow onclick right-------------------------------------------------------------------------------------------------------------------

  const nextSlide = () => {
    // a t√∂mb hossza ahol az adatok vannak
    if (slideIndex !== dataSlider.length) {
      setSlideIndex(slideIndex + 1);
      AnimatedBox();
      ActiveDotWithArrows(slideIndex + 1);
    } else if (slideIndex === dataSlider.length) {
      setSlideIndex(1);
      AnimatedBox();
      ActiveDotWithArrows(1);
    }
  };

  // slider arrow onclick left  -------------------------------------------------------------------------------------------------------

  const prevSlide = () => {
    if (slideIndex !== 1) {
      setSlideIndex(slideIndex - 1);
      AnimatedBox();
      ActiveDotWithArrows(slideIndex - 1);
    } else if (slideIndex === 1) {
      setSlideIndex(dataSlider.length);
      AnimatedBox();
      ActiveDotWithArrows(dataSlider.length);
    }
  };

  //---------------------------------------------------------------------------------------------------------------------------------------------
  // education hide or show
  const showEdu = () => setEdu(true);
  const hideEdu = () => setEdu(false);

  function educationToggler() {
    setEdu((state) => !state);
  }
  useEffect(() => {
    downA = document.querySelector(".downArrow");
    upA = document.querySelector(".upArrow");
  }, []);
  // ---------------------------------------------------------------------
  // ----------------- onclick animation--------------------------------

  const AnimatedBox = () => {
    // box animation begins
    setAnimation(true);

    // box animation stops after 2 seconds
    setTimeout(() => setAnimation(false), 2000);

    console.log(animation);
  };

  // ---------------------------------------------------------------------

  //-----------------------------------------------------------

  function changeMode() {
    setDarkMode(!darkMode);
    console.log(darkMode);
  }

  function changeLanguage() {
    setHungarian(!hungarian);
    console.log(hungarian);
  }

  // function scroll() {
  //   const element = event.target.id;
  //   console.log(element);
  //   document
  //     .getElementById("skills")
  //     .scrollIntoView(true, { block: "start", inline: "nearest" });
  // }

  // button scrolling effects--------------------------------------------------------------
  // a projekt v√©g√©n foglalkozni vele.
  // function addScroll() {
  //   const navButtons = document.querySelectorAll(".navButton");
  //   console.log(navButtons);
  //   for (let i = 0; i < navButtons.length; i++) {
  //     navButtons[i].addEventListener("click", function () {
  //       document
  //         .getElementById(`${navButtons[i].innerHTML}`)
  //         .scrollIntoView(true, { block: "start", inline: "nearest" });
  //     });
  //   }
  // }
  return (
    <>
      <div className="container">
        <nav>
          <div className="navigation">
            <button className="navButton">S√∂men D√°niel</button>
            <button className="navButton">introduction</button>
            <button className="navButton">aboutMe</button>
            <button className="navButton">informations</button>
            <button className="navButton">experience</button>
            <button className="navButton">projects</button>
            <button className="navButton">education</button>
            <button className="navButton">skills</button>
          </div>
          <div className="modes">
            <button onClick={changeLanguage} className="language">
              {`Language: ${hungarian === false ? "English" : "Hungarian"}`}
            </button>
            <button onClick={changeMode} className="displayMode">
              {`Theme: ${darkMode === false ? "Dark üåë" : "Light üí°"}`}
            </button>
          </div>
        </nav>
        <section className="introduction">
          <div className="container">
            <img className="profilePicture" src={profilePicture}></img>
            <h1 className="name">Hi, I'm Daniel</h1>
            <h3 className="title">foglalkoz√°s</h3>
          </div>
        </section>
        <section className="aboutMe">
          <div className="aboutMeContainer">
            <h1>Magamr√≥l</h1>
            <div className="cont">
              <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc ut
                ex elit. Mauris tincidunt pharetra lectus, consectetur imperdiet
                leo condimentum et. Donec metus nibh, laoreet sed aliquet vitae,
                ultrices laoreet massa. Nam ut elementum ante, eget luctus orci.
                Curabitur id accumsan odio. Sed id quam vitae mauris interdum
                convallis eu ac nulla. Aliquam erat volutpat. Vestibulum
                volutpat ut tortor id cursus. In ac nulla commodo, dignissim
                lectus vel, egestas tortor. Curabitur molestie congue varius.
                Fusce faucibus lectus nec porttitor tempus. Etiam et facilisis
                ex. Nam eu magna in tortor tempus volutpat ut nec urna. Nunc id
                neque velit. Curabitur pulvinar odio et nulla mollis, sit amet
                consequat sem luctus. Ut porta elit sed nunc mollis tincidunt.
                Vestibulum quis porta dui, in venenatis mi. Suspendisse
                consectetur interdum elit ut pellentesque. Aliquam a ullamcorper
                odio, sed aliquam est.
              </p>
            </div>
          </div>
        </section>
        <section className="experience">
          <h1>munka tapasztalat</h1>
          <div className="cont">
            <div className="workTempCont">
              <div className="left">
                <WorkDisplayTemplate />
              </div>
              <div className="middle">
                <WorkDisplayTemplate />
              </div>
              <div className="right">
                <WorkDisplayTemplate />
              </div>
            </div>
          </div>
        </section>
        {/* ----------------------------------------------------------------projects------------------------------------------------------------------- */}

        {/* --------------missing slideing animation.------- */}
        <section className="projects">
          <h1>projektek</h1>
          <div className="ProjectContainer">
            <button onClick={prevSlide} className="leftArrow">
              <img className="lRArr" src={leftArrow}></img>
            </button>
            <ProjectTemplate
              picture={profilePicture}
              title={dataSlider[slideIndex - 1].pName}
              description={dataSlider[slideIndex - 1].pDescription}
              technologies={dataSlider[slideIndex - 1].pTechnologies.join(" ")}
              links={dataSlider[slideIndex - 1].pLinks}
              class={animation}
              activate={ActiveDot}
            />
            <button onClick={nextSlide} className="rightArrow">
              <img className="lRArr" src={rightArrow}></img>
            </button>
            <div className="dots">
              <button onClick={ActiveDot} id="active" className="dot1"></button>
              <button onClick={ActiveDot} className="dot2"></button>
              <button onClick={ActiveDot} className="dot3"></button>
              <button onClick={ActiveDot} className="dot4"></button>
              <button onClick={ActiveDot} className="dot5"></button>
            </div>
          </div>
        </section>
        {/* ----------------------------------------------------------------education------------------------------------------------------------------- */}
        <section className="education">
          <div>
            <h1>k√©pzetts√©g</h1>
            {edu ? (
              <div className="educationInfo">
                <EducationDisplayTemplate />
              </div>
            ) : null}
            {edu ? null : <p>click to expand.</p>}
            {edu ? null : (
              <button onClick={showEdu} className="downArrow">
                <img className="dArr" src={downArrow}></img>
              </button>
            )}
            {edu ? (
              <button onClick={hideEdu} className="downArrow">
                <img className="dArr" src={upArrow}></img>
              </button>
            ) : null}
          </div>
        </section>
        <section className="skills" id="skills">
          <h1 className="skillsTitle">skills</h1>
          <div className="skillsContainer">
            <h1 className="languages">languages</h1>
            <div className="languageContainer">
              <div className="Hun">
                <p>Hungarian</p>
                <p>native</p>
              </div>
              <div className="En">
                <p>English</p>
                <p>intermediate. Language certificate B2</p>
              </div>
            </div>
            <h1 className="progTitle">some thing i have used</h1>
            <div className="progSkillsList">
              <ul>
                <li>JS</li>
                <li>React</li>
                <li>HTml</li>
              </ul>
              <ul>
                <li>Css</li>
                <li>git</li>
                <li>github</li>
              </ul>
              <ul>
                <li>scrum</li>
                <li>bootstrap</li>
                <li>Slack</li>
              </ul>
            </div>
            <p className="SkillsFooter">
              valami sz√∂veg arr√≥l h folyamatosan fejl≈ëdni szeretn√©k
            </p>
          </div>
        </section>
        <footer>
          <h1>Footer</h1>
          <div className="footerContainer">
            <h1>somendaniel7@gmail.com</h1>
            <div className="footerDatas">
              <div className="place">
                <p>Budapest, hungary</p>
              </div>
              <div className="footerButtons">
                <button>linkedin</button>
                <button>linkedin</button>
              </div>
              <div className="signature">
                <p>2022 | Designed & coded by D√°niel S√∂men. &#169;</p>
              </div>
            </div>
          </div>
        </footer>
      </div>
    </>
  );
}

export default MainPage;
