import react from "react";
import "./MainPage.css";
import profilePicture from "../images/SD.jpg";
import { useState } from "react";
import { useEffect } from "react";
import WorkDisplayTemplate from "./WorkDisplayTemplate";
import downArrow from "../images/downArrow.png";
import rightArrow from "../images/downArrow.png";
import leftArrow from "../images/downArrow.png";
import upArrow from "../images/upArrow.png";
import EducationDisplayTemplate from "./EducationDisplayTemplate";

function MainPage() {
  const [darkMode, setDarkMode] = useState(false);
  const [hungarian, setHungarian] = useState(false);
  const [edu, setEdu] = useState(false);
  let upA;
  let downA;

  // function starter() {
  //   document.body.addEventListener("DOMContentLoaded", function () {
  //     upA.style.display = "none";
  //     downA.style.display = "inline-block";
  //   });
  // }

  console.log(edu);
  function educationToggler() {
    setEdu((state) => !state);
    console.log(edu);
  }
  useEffect(() => {
    downA = document.querySelector(".downArrow");
    upA = document.querySelector(".upArrow");
  }, []);

  function changeMode() {
    setDarkMode(!darkMode);
    console.log(darkMode);
  }

  function changeLanguage() {
    setHungarian(!hungarian);
    console.log(hungarian);
  }

  // function scroll() {
  //   const element = event.target.id;
  //   console.log(element);
  //   document
  //     .getElementById("skills")
  //     .scrollIntoView(true, { block: "start", inline: "nearest" });
  // }

  // button scrolling effects--------------------------------------------------------------
  // a projekt v√©g√©n foglalkozni vele.
  // function addScroll() {
  //   const navButtons = document.querySelectorAll(".navButton");
  //   console.log(navButtons);
  //   for (let i = 0; i < navButtons.length; i++) {
  //     navButtons[i].addEventListener("click", function () {
  //       document
  //         .getElementById(`${navButtons[i].innerHTML}`)
  //         .scrollIntoView(true, { block: "start", inline: "nearest" });
  //     });
  //   }
  // }

  return (
    <>
      <div className="container">
        <nav>
          <div className="navigation">
            <button className="navButton">S√∂men D√°niel</button>
            <button className="navButton">introduction</button>
            <button className="navButton">aboutMe</button>
            <button className="navButton">informations</button>
            <button className="navButton">experience</button>
            <button className="navButton">projects</button>
            <button className="navButton">education</button>
            <button className="navButton">skills</button>
          </div>
          <div className="modes">
            <button onClick={changeLanguage} className="language">
              {`Language: ${hungarian === false ? "English" : "Hungarian"}`}
            </button>
            <button onClick={changeMode} className="displayMode">
              {`Theme: ${darkMode === false ? "Dark üåë" : "Light üí°"}`}
            </button>
          </div>
        </nav>
        <section className="introduction">
          <div className="container">
            <img className="profilePicture" src={profilePicture}></img>
            <h1 className="name">Hi, I'm Daniel</h1>
            <h3 className="title">foglalkoz√°s</h3>
            <div className="links">
              <a
                class="github-button"
                href="https://github.com/SomenDaniel"
                data-color-scheme="no-preference: light; light: light; dark: dark_high_contrast;"
                aria-label="Follow @SomenDaniel on GitHub"
              >
                Follow @SomenDaniel
              </a>
              <a
                href="https://www.linkedin.com/in/d%C3%A1niel-s%C3%B6men-695411217/"
                target="_blank"
              >
                <button className="linkedin">LinkedIn</button>
              </a>
            </div>
          </div>
        </section>
        <section className="aboutMe">
          <h1>Magamr√≥l</h1>
          <div className="cont">
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nunc ut
              ex elit. Mauris tincidunt pharetra lectus, consectetur imperdiet
              leo condimentum et. Donec metus nibh, laoreet sed aliquet vitae,
              ultrices laoreet massa. Nam ut elementum ante, eget luctus orci.
              Curabitur id accumsan odio. Sed id quam vitae mauris interdum
              convallis eu ac nulla. Aliquam erat volutpat. Vestibulum volutpat
              ut tortor id cursus. In ac nulla commodo, dignissim lectus vel,
              egestas tortor. Curabitur molestie congue varius. Fusce faucibus
              lectus nec porttitor tempus. Etiam et facilisis ex. Nam eu magna
              in tortor tempus volutpat ut nec urna. Nunc id neque velit.
              Curabitur pulvinar odio et nulla mollis, sit amet consequat sem
